package day1;

import java.util.*;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 4;
		double d = 4.0;
		String s = "HackerRank ";

		Scanner scan = new Scanner(System.in);
		
		int j;
		double e;
		String t;
		
		j = scan.nextInt();
		e = scan.nextDouble();
		scan.nextLine();
		t = scan.nextLine();
		
		System.out.println((i + j));
		System.out.println((d + e));
		System.out.println((s + t));
	}

}
